package ch2;

// 논리 부정 연산 : ! => boolean 타입만 사용
// 거꾸로 만들어 줌 (true -> false, false -> true)

public class DenyLogicOperatorEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean play = true;
		
		play = !play;
		System.out.println(play);
		
	}

}
